package mypackage;

public class Dowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		do
		{
			System.out.print(" "+i);
			i++;
		}while(i <= 31);
		
		char c ='A';
		while(c <= 'Z')
		{
			System.out.print(" "+c);
			c++;
		}
	}

}
